    customElements.define('sidebar-slide', class extends Block {

    init() {
        this.slide = this.select('div');
        this.slide.addEventListener('click', () => {
            this.fire('slide-click', this.data);
        }, false);
        this.attachEvent('slide-delete');
        this.attachEvent('slide-copy');
        this.attachEvent( 'slide-down');
        this.attachEvent( 'slide-up');
    }

    attachEvent(eventAndClass) {
        if (this.dataset.editor !== "true")
            return
        let btn = this.select('.' +eventAndClass);
        btn && btn.addEventListener('click', e => {
            e.stopPropagation();
            e.preventDefault();
            this.fire(eventAndClass, this.data)
        })
    }

    selectSlide(b) {
        this.select('div').classList.toggle('selected', b)
    }

    html() {
        let d = this.data
        let height = this.getAttribute('height')
        let slideButtons = this.dataset.editor === "true" ? `
            <span class="button slide-delete" title="Удалить">✖</span>
            <span class="button slide-copy" title="Копия">C</span>
            
            ${d.last ? '' : '<span class="button slide-down" title="Вниз">▼</span>'}
            ${d.index ? '<span class="button slide-up" title="Вверх">▲</span>' : ''}
            
        ` : "";


        return `
            <style>
                :host {
                    display: block; 
                    width: 100%; 
                    height: ${height}; 
                }
                :host > div.selected {
                    box-shadow: 0 0 2px 2px rgb(255, 83, 0);
                    transform: scale(1.02);
                }
                :host > div {
                    cursor: pointer;
                    display: block; 
                    --h: calc(${height} - 25px);
                    --w: calc(var(--h) / 0.618);
                    width: var(--w); 
                    height: var(--h);
                    background: white;  
                    margin-left: calc(0.5 * calc(100% - var(--w)));
                    /*transition: 0.2s;*/
                    ;
                }
  
                .index {
                   width: 100%;
                   height: calc(${height} - 85px);
                   text-align: center;
                   line-height: 50px;
                   font-size: 40px;
                   font-family: Arial, sans-serif;
                   pointer-events: none;
                }
                
                :host > div.selected .button{
                    color: lightgray;
                    pointer-events: auto;
                }
                .button {
                   float: right;
                   display: inline-block; 
                   width: 20px;
                   height: 25px;
                   text-align: center;
                   line-height: 25px;
                   font-size: 20px;
                   font-family: Arial, sans-serif;
                   top: 0;
                   right: 0;
                   color: white;
                   transition: 200ms;
                   pointer-events: none;
                }
                
                .button.slide-copy {
                    font-size: 17px;
                }
                
                :host > div.selected .button:hover {
                    color: black;
                }
                
                .row-30px {
                    height: 30px;
                }
            </style>
            <div>
                <div class="row-30px">${slideButtons}</div>
                <div class="index">${d.index+1}</div>
                <div class="row-30px"></div>
            </div>
        `;
    }
});


        
    // ./src/SlidesSidebar.js
    
    customElements.define('slides-sidebar', class extends Block {

    updateSidebar() {
        let width = this.getAttribute('width')

        let addButton = this.dataset.editor==="true" ? '<event-button event="add-slide"  title="Добавить слайд">+</event-button>' : '';

        this.shadowRoot.innerHTML = `
            <style>
                :host {
                    display: block;
                    float: left;
                    width: ${width};
                    height: 100%;
                    max-height: 100%;
                    overflow-y: scroll;
                    background: #333;
                    border-left:1px solid gray;
                    box-sizing: border-box;
                }
            </style>
            <div style="height: 10px"></div>
            <div id="list"></div>
            ${addButton}
        `;

        if ( !this.data || !this.data.slides)
            return

        this.slides = this.data.slides.map((slideData, i) => {
            let slide = document.createElement('sidebar-slide');
            slideData.index = i
            slideData.last = i === this.data.slides.length - 1;
            slide.setData(slideData);
            slide.dataset.editor = this.dataset.editor
            slide.setAttribute('height', `calc(0.618 * ${width})`)
            this.shadowRoot.querySelector('#list').append(slide);
            return slide;
        })
    }

    selectSidebarSlide(slide){
        this.slides && this.slides.forEach(s => s.selectSlide(s.data.index === slide.index))
    }
});


        
