    customElements.define('viewer-text-panel', class extends Block {

    init(el) {
        addEventListener('resize', () => this.fitToScreen());
    }

    fitToScreen(){
        let presentationsViewer = document.querySelector('presentations-viewer');
        if (!presentationsViewer)
            return
        let rect = presentationsViewer
            .getBoundingClientRect();
        if (!rect)
            return
        let sidebar = parseFloat(this.getAttribute('sidebarSize'));
        let w = rect.width - 40 - sidebar
        let div = this.select('div');
        div.style.width = w + "px"
        this.style.top = rect.y + rect.height -200+ "px"
    }

    html() {
        let width = this.getAttribute('width')

        return `
            <style>
        
                :host{
                    display: block;
                    position: absolute;
                }
                
                div {
                    width: 0;
                    height: 200px;
                    display: block;
                    background: white;
                    font-family: Arial, sans-serif;
                    overflow-y: auto;
                    margin-left: 20px;
                    margin-right: 20px;
                    border-radius: 6px;
                }
                
                .hidden {
                    display: none;
                }
            </style>
            <div></div>
        `;
    }

    togglePanel(b) {
        let classList = this.select('div').classList;
        if (b === undefined){
            classList.toggle('hidden')
        } else{
            classList.toggle('hidden', b)
        }
        return classList.contains('hidden')
    }

    setContent(slide){
        let div = this.select('div');
        div.innerHTML = slide.pdf;
        div.innerHTML = `<span style="font-size: 30px">Cлайд ${slide.index+1}</span><br><br>` +
            [...div.querySelectorAll('.page > .template')].map(b => {
                return `<b>${b.getAttribute('block_name')}</b> ${b.getAttribute('block_descr')||''}`
            }).join("<br><br>");
        setTimeout(()=>this.fitToScreen(),200)
    }
});


        
