    customElements.define('presentations-editor', class extends Block {

    initEditor(editorProperties){
        let sidebarSize = (editorProperties.sidebarWidth || 218) + 'px'
        this.shadowRoot.innerHTML = `
            <style>
                :host {
                    display: block;
                    width: 100%; 
                    float: left;
                    background-color: black;
                }
            </style>
            <editor-area width="calc(100% - ${sidebarSize})"></editor-area>
            <slides-sidebar width="${sidebarSize}" data-editor="true"></slides-sidebar>
        `;

        this.editor = this.select('editor-area');
        this.editor.initEditorArea(editorProperties);
        this.sidebar = this.select('slides-sidebar');

        this.on('add-slide', () => {
            this.editor.saveCurrentSlide()
            let newSlide = this.newSlide()
            this.sidebar.updateSidebar();
            this.editor.setData(newSlide)
            this.sidebar.selectSidebarSlide(newSlide)
        });

        this.on('slide-click', slide => {
            setTimeout(() => {

                if (this.editor.data && this.editor.data.id === slide.id)
                    return

                this.editor.saveCurrentSlide()
                this.editor.setData(this.data.slides.find(s => s.id === slide.id))
                this.sidebar.selectSidebarSlide(slide)
            }, 10)
        });

        this.on('slide-delete', e => {
            this.data.slides.splice(e.index, 1);
            let newSlide = this.data.slides[e.index-1];
            this.editor.setData(newSlide);
            this.sidebar.updateSidebar();
            this.sidebar.selectSidebarSlide(newSlide)
        });

        this.on('slide-copy', e => {
            let targetSlide = this.data.slides.find(s => s.id === e.id);
            let targetIndex =  this.data.slides.indexOf(targetSlide);
            targetSlide = {
                id: this.rnd(),
                pdf: targetSlide.pdf
            }
            this.data.slides.splice(targetIndex, 0, targetSlide)
            this.editor.setData(targetSlide);
            this.sidebar.updateSidebar();
            this.sidebar.selectSidebarSlide(targetSlide)
        });

        this.on('slide-up', e => this.moveSlide(e, -1));
        this.on('slide-down', e => this.moveSlide(e, 1));
    }

    moveSlide(e, dir){
        let targetSlide = this.data.slides.find(s => s.id === e.id);
        this.data.slides.splice(e.index, 1);
        this.data.slides.splice(e.index+dir, 0, targetSlide)
        this.sidebar.updateSidebar();
        this.sidebar.selectSidebarSlide(targetSlide)
    }

    newSlide() {
        let slide = {id: this.rnd(), pdf:`<div class="page container"></div>`};
        this.data.slides.push(slide);
        return slide
    }

    getPresentation() {
        this.editor.saveCurrentSlide()
        return JSON.stringify(this.data);
    }

    ensureFirstSlide(){
        if (!this.data.slides) {
            this.data.slides = [];
        }

        if (!this.data.slides.length) {
            this.newSlide();
        }
    }

    setPresentation(data) {
        super.setData(JSON.parse(data));
        this.ensureFirstSlide()
        this.sidebar.setData(this.data);
        this.sidebar.updateSidebar();
        let firstSlide = this.data.slides[0];
        this.sidebar.selectSidebarSlide(firstSlide)
        this.editor.setData(firstSlide)
    }

    rnd() {
        return Math.random().toString(36).substring(2)
    }

});




        
