    customElements.define('presentations-viewer', class extends Block {
    html() {
        let sidebarSize = "218px"
        let _100_minus_width = ` width="calc(100% - ${sidebarSize})" `;
        return `
            <style>
                :host {
                    display: block;
                    width: 100%; 
                    float: left;
                    background-color: #555;
                }
            </style>
            <viewer-area ${_100_minus_width}></viewer-area>
            <slides-sidebar width="${sidebarSize}" ></slides-sidebar>
            <viewer-text-panel ${_100_minus_width} sidebarSize="${sidebarSize}"></viewer-text-panel>
        `;
    }

    init(el) {
        this.viewer = this.select('viewer-area');
        this.sidebar = this.select('slides-sidebar');
        this.text = this.select('viewer-text-panel');
        this.on('slide-click', e => this.selectSlide(e));


        setInterval(() => this.restoreActualSlide(), 100)
        setInterval(() => this.updateActivityTimestamp(), 1000)
        document.addEventListener('fullscreenchange', () => this.changeFullscreen());
        document.addEventListener('webkitfullscreenchange', () => this.changeFullscreen());
        document.addEventListener('mozfullscreenchange', () => this.changeFullscreen());
    }

    changeFullscreen() {
        if (!document.fullscreenElement)
            this.toggleTextPanel(!this.textPanelVisibilityBeforeFullScreen);
    }

    restoreActualSlide(){
        if (!this.data || !this.data.slides)
            return
        let slide = localStorage.getItem('presentation-viewer-slide');
        if (this.currentSlide === slide)
            return;
        this.currentSlide = slide
        slide = this.data.slides.find(s => s.id === slide);
        if (!slide)
            slide = this.data.slides[0]

        this.viewer.setData(slide);
        this.sidebar.selectSidebarSlide(slide)
        this.text.setContent(slide)
    }

    updateActivityTimestamp(){
        localStorage.setItem('presentation-viewer-last-activity-timestamp', ''+Date.now())
    }

    selectSlide(slide) {
        localStorage.setItem('presentation-viewer-slide', slide.id);
    }

    setPresentation(data) {
        data = JSON.parse(data);
        this.setData(data);
        this.sidebar.setData(data);
        this.sidebar.updateSidebar();
        let ts = +localStorage.getItem('presentation-viewer-last-activity-timestamp');
        if (Date.now() - ts > 1500)
            this.selectSlide(this.data.slides[0]);
    }

    requestFullScreen(){
        this.textPanelVisibilityBeforeFullScreen = this.viewer.textPanelSize
        this.toggleTextPanel(true);
        this.viewer.requestFullScreen()
    }

    toggleTextPanel(b){
        let hidden = this.text.togglePanel(b);
        this.viewer.updateSize(hidden?0:1);
    }

});
    
